select  a.instance,a.login,a.primary_email,last_active,IF(a.suspended>0, 'true','false') AS suspended, IF(dormant>0, 'true','false') AS dormant,last_dashboard_event_time,last_audit_log_entry_time,last_web_session_time,date(last_update_time) AS last_update_time  from users a left join ( select instance,login, max(last_update_time) as last_update_time from (select instance,login,last_audit_log_entry_time as last_update_time from users union select instance,login,last_dashboard_event_time from users  union select instance,login,last_web_session_time from users) us group by us.instance, us.login) b on a.instance = b.instance and a.login = b.login where ldap_hpeSpinCompany='Seattle' and a.instance = 'partner';  
